def sumup_percent(dictionary):
    in_degree = list(dict(dictionary).keys())
    num = list(dict(dictionary).values())
    y = [(a/ sum(num)) for a in num]
    z = dict(zip(in_degree,y))
    perc_list = list(z.values())
    sumup = sum(perc_list[0:11])
    print("In_degree from0 to 10:",sumup)
    return sumup

